mapper.update:
update comiclabel
set cl_name = #{clName,jdbcType=VARCHAR},
  start_time = #{startTime,jdbcType=TIMESTAMP},
  end_time = #{endTime,jdbcType=TIMESTAMP}
where cl_id = #{clId,jdbcType=INTEGER}

mapper.select:
select cl_id, cl_name, start_time, end_time
from comiclabel
where cl_id = #{clId,jdbcType=INTEGER}
