mapper.update:
update roleinfo
set role_name = #{roleName,jdbcType=VARCHAR},
  start_time = #{startTime,jdbcType=VARCHAR},
  end_time = #{endTime,jdbcType=VARCHAR}
where role_id = #{roleId,jdbcType=INTEGER}

mapper.select:
select role_id, role_name, start_time, end_time
from roleinfo
where role_id = #{roleId,jdbcType=INTEGER}
