mapper.select:
SELECT video_id videoId, video_name videoName, video_image videoImage, video_state videoState, video_type videoType, video_info videoInfo, video_upOfUser videoUpofuser, video_UpTime videoUptime, playSum,collectSum,supportSum,comentSum FROM video WHERE video_upOfUser = #{videoUpofuser}
and video_state like CONCAT('%', '', '%')
and video_state = #{videoState}
order by video_UpTime desc
