mapper.update:
update comicorlabel
set cl_id = #{clId,jdbcType=INTEGER},
  start_time = #{startTime,jdbcType=TIMESTAMP},
  end_time = #{endTime,jdbcType=TIMESTAMP}
where col_id = #{colId,jdbcType=INTEGER}

mapper.select:
select col_id, cl_id, start_time, end_time
from comicorlabel
where col_id = #{colId,jdbcType=INTEGER}
