mapper.update:
update videolabel
set vl_name = #{vlName,jdbcType=VARCHAR},
  start_time = #{startTime,jdbcType=TIMESTAMP},
  end_time = #{endTime,jdbcType=TIMESTAMP}
where vl_id = #{vlId,jdbcType=INTEGER}

mapper.select:
select vl_id, vl_name, start_time, end_time
from videolabel
where vl_id = #{vlId,jdbcType=INTEGER}
